mapper.select:
select station_g_cd, station_name_cn, station_name, station_name_r from ( select station_g_cd, station_name_cn, station_name, station_name_r, e_sort as hit_sort from station where e_status = '0' and pref_cd in (11, 12, 13, 14) and station_name like #{stationNameLikeSQL_1}
union select station_g_cd, station_name_cn, station_name, station_name_r, e_sort * 10 as hit_sort from station where e_status = '0' and pref_cd in (11, 12, 13, 14) and (station_name like #{stationNameLikeSQL_2} or station_name like #{stationNameLikeSQL_3} )
) as temp order by hit_sort limit 10

	select
		userID
	from stationHistory
    where userID = #{open_id}
    and station_g_cd = #{station_g_cd}

mapper.update:
	update stationHistory set
		selectDate = now()
    where userID = #{open_id}
    and station_g_cd = #{station_g_cd}

mapper.delete:
	delete
	from stationHistory
    where userID = #{open_id}
    and station_g_cd = #{station_g_cd}
